interface IState {
  name: string
  age: number
  hobby: string[]
}
const UseObjStateExample = () => {
  const [state, setState] = useObjState<IState>({
    name: 'hezebin',
    age: 0,
    hobby: [],
  })

  return (
    <div>
      <div>obj state: {JSON.stringify(state)}</div>
      <br />
      <Button
        onClick={() =>
          setState({
            name: 'hezebin change',
          })
        }>
        set name
      </Button>
      <Button
        onClick={() =>
          setState((prev) => {
            return { age: prev.age + 1, hobby: [...prev.hobby, 'test'] }
          })
        }>
        add age: {state.age + 1}
      </Button>
    </div>
  )
}

